import React, {Component} from 'react';
import PostsList from './PostsList/PostsList';
import CreatePost from './CreatePost/CreatePost'

class Posts extends Component {
    constructor () {
        super();

        this.state = {
            posts: []

        }
    }

    //get Posts from server
    getPosts = async () => {
        const posts = await fetch('http://localhost:9000/posts');
        const postsJSON = await posts.json();
        return postsJSON
      }

    // Component Did Mount Check
      componentDidMount(){
        this.getPosts().then((posts) => {
          this.setState({posts: posts.data})
        }).catch((err) => {
          console.log(err);
        })
      }



    // Add a Post function to be passed down to child

    addPost = async (post, e) => {

        e.preventDefault();
        console.log(post);

        try {


              const createdPost = await fetch('http://localhost:9000/posts/', {
              method: 'POST',
              body: JSON.stringify(post),
              headers: {
                'Content-Type': 'application/json'
              }
            });

            const parsedPost = await createdPost.json();
            console.log(parsedPost)

            this.setState({posts: [...this.state.posts, parsedPost.data]})


        } catch (err) {

        }

    }

    render() {

        return (

            <div>
                <CreatePost addPost = {this.addPost}/>
                <PostsList />
            </div>
        )

    }



}

export default Posts;
