import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker, InfoWindow } from 'google-maps-react';

const mapStyles = {
  width: '100%',
  height: '100%'
};

export class MapContainer extends Component {
    constructor() {
        super();
        const { lat, lng } = this.props.initialCenter;
         this.state = {
               events : [],
               currentLocation: {
               lat: lat,
               lng: lng
            },
            showingInfoWindow: false,  
            activeMarker: {},         
            selectedPlace: {} 
          };

    }

    onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };


  render() {

    const allSearchResults = this.props.searchResults.map((searchResult, i) =>{
      console.log(searchResult);
      return (<Marker key={i} title={searchResult.displayName} 
        position={{lat: searchResult.location.lat,
            lng: searchResult.location.lng
        }}
       />)
    });

    return (
      <Map
        google={this.props.google}
        zoom={14}
        style={mapStyles}

     >
        <Marker
          onClick={this.onMarkerClick}
         allSearchResults= {allSearchResults}

        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
      </Map>
    );
  }

}


export default GoogleApiWrapper({
  apiKey: 'AIzaSyB3cnf0QZnOYfHCu1eElopL5grod6fyePU'
})(MapContainer);

MapContainer.defaultProps = {
  zoom: 14,
  initialCenter: {
    lat: 39.7392,
    lng: 104.9903
  },
  centerAroundCurrentLocation: false,
  visible: true
};
