import React, { Component } from 'react';
import './App.css';
import Posts from './PostsContainer/PostsContainer';
import EventContainer from './EventContainer/EventContainer';
import NavContainer from './NavContainer/NavContainer'
import { Col, Container, Row} from 'reactstrap';
import Login from './Login/Login';
import {Route, Switch} from 'react-router-dom';
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react';

library.add(faSearch)


const My404 = () => {
  return (
    <div>
      You're Lost
    </div>
    )
};
class App extends Component {
  constructor () {
    super()
    this.state = {
      loggedIn: false,
      currentUser: {
        username: '',
        password: ''
      },
      error: ""
    }
  }

  handleInputs = (e) => {
  this.setState({
    [e.currentTarget.name]: e.currentTarget.value
  })
  }
  handleLogin = async (formData) => {
    const newUser = await fetch("http://localhost:9000/auth/login", {
      method: "POST",
      credentials: 'include',
      body: JSON.stringify(formData),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const response = await newUser.json();
    console.log(response);
    if(response.status === 200){
      this.setState({
        loggedIn: true,
        currentUser: response.data
      })
    }
  }

  handleRegistration = async (formData) => {
    const newUser = await fetch("http://localhost:9000/users", {
      method: "POST",
      credentials: 'include',
      body: JSON.stringify(formData),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const response = await newUser.json();
    console.log(response);
    if(response.status === 200){
      this.setState({
        loggedIn: true,
        currentUser: response.data
      })
    }else if(response.status === 500){
      console.log(response.data);
      if(response.data.code === 11000){
        this.setState({
          error: "INVALID DUPLICATE USER NAME"
        })
      }else{
              this.setState({
        error: response.data.message
          })
      }

    }
  }


   render() {
    return (
      <div className="App">

      <NavContainer />

          { this.state.loggedIn ? 
          <div>
            <Row>


                  <img src="live-music2.jpeg" className="live-music"/>

            </Row>

            <Container>
              <Row>
                <Col xs="6">
                  <Posts /> 
                </Col>                  
                <Col xs="6">
                  <EventContainer />
                </Col>
              </Row>
              <Row>
              </Row>


            </Container>


          </div>: 


            <Login handleRegistration={this.handleRegistration} handleInputs={this.handleInputs} handleLogin={this.handleLogin} />}


      </div>
    );
  }
}

export default App;
