import React, {Component} from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Label, Input, Form} from 'reactstrap';


class CreatePost extends Component {
    constructor () {
        super();

        this.state = {
            title: '',
            commentBody: ''
        }
    }

    handleInput = (e) => {
        this.setState({
            [e.currentTarget.name]: e.currentTarget.value
        })

    }

    render () {
        return (

            <div>
                <h2>Create a Post</h2>
                <Form id="new-post-form" onSubmit={this.props.addPost.bind(null, this.state)} >
                    <FormGroup>
                        <Label for="title">Post Title</Label>
                        <Input input type="text" name="title" value={this.state.title} onChange={this.handleInput}/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="commentBody">Comment</Label>
                        <Input input type="text" name="commentBody" value={this.state.commentBody} onChange={this.handleInput}/>
                    </FormGroup>
                    <Button input type="submit" value="Submit">Submit</Button> 
                </Form>
            </div>
        )
    }



}
export default CreatePost;
