import React, { Component } from 'react';
import './App.css';
import Posts from './PostsContainer/PostsContainer';
import EventContainer from './EventContainer/EventContainer';
import NavContainer from './NavContainer/NavContainer'
import { Col, Container, Row} from 'reactstrap';
import Login from './Login/Login';
import {Route, Switch} from 'react-router-dom';


const My404 = () => {
  return (
    <div>
      You're Lost
    </div>
    )
};


class App extends Component {
  constructor () {
    super()
      this.state = {
        username: "",
        password: "",
        location: 10000,
        loggedIn: false
      }
  }

  handleInputs = (e) => {
  this.setState({
    [e.currentTarget.name]: e.currentTarget.value
  })
  }

  submitRegistration = async (e) => {
  e.preventDefault();
  console.log("GOT HERE")
  console.log(this.state);
  try{
    const createUser = await fetch('http://localhost:9000/auth/register', {
      method: 'POST',
      body: JSON.stringify(this.state),
      headers: {
        'Content-Type': 'application/json'
      } 
    });
    const parsedResponse = await createUser.json();
    console.log(parsedResponse, ' this is response')
    if(parsedResponse.status == 200){
      this.setState({
        loggedIn: true,
        // this isn't a real login - need to align it with the back-end to sort that out
        username: parsedResponse.data.username,
        location: parsedResponse.data.location
      })
    } else if (parsedResponse.status == 500){
      console.log("INTERNAL SERVER ERROR")
    }
  }catch(err){
    console.log(err, " error")
  }
  }


   render() {
    return (
      <div className="App">

      <NavContainer />

          < EventContainer /> 
          { this.state.loggedIn ? <Posts username={this.state.username} location={this.state.location} /> : <Login submitRegistration={this.submitRegistration} handleInputs={this.handleInputs} />}


      </div>
    );
  }
}

export default App;
