import React, {Component} from 'react';
import PostsList from './PostsList/PostsList';
import CreatePost from './CreatePost/CreatePost';
import EditPost from './EditPost/EditPost'

class Posts extends Component {
    constructor () {
        super();

        this.state = {
            posts: [], 
            postToEdit : {
                title: '',
                commentBody: '', 
                _id: ''
            }

        }
    }

    //get Posts from server
    getPosts = async () => {
        const posts = await fetch('http://localhost:9000/posts');
        const postsJSON = await posts.json();
        return postsJSON
      }

    // Component Did Mount Check
      componentDidMount(){
        this.getPosts().then((posts) => {
          this.setState({posts: posts.data})
        }).catch((err) => {
          console.log(err);
        })
      }



    // Add a Post function to be passed down to child

    addPost = async (post, e) => {

        e.preventDefault();
        console.log(post);

        try {


              const createdPost = await fetch('http://localhost:9000/posts/', {
              method: 'POST',
              body: JSON.stringify(post),
              headers: {
                'Content-Type': 'application/json'
              }
            });

            const parsedPost = await createdPost.json();
            console.log(parsedPost)

            this.setState({posts: [...this.state.posts, parsedPost.data]})


        } catch (err) {

        }

    }


    // Delete Post function 
    deletePost = async (id) => {

        try {
            const deletedPost = await fetch('http://localhost:9000/posts/' + id, {
                method: 'DELETE'
            });

            const deletedPostJSON = await deletedPost.json();

            this.setState({posts: this.state.posts.filter((post)=> post._id !== id)})

            console.log(deletedPostJSON)

        } catch (err) {

        }


    }


    // Edit Post 

    handleEdit = async (e) => {
        this.setState({
            postToEdit : {
                ... this.state.postToEdit,
                [e.currentTarget.name]: e.currentTarget.value
            }
        })
    }

    editPost = async (id) => {
        e.preventDefault();
        try {
            const editedPost = await fetch ('http://localhost:9000/posts/' + this.state.postToEdit._id, {
                method: 'PUT', 
                body: JSON.stringify({
                    title: this.state.postToEdit.title,
                    commentBody: this.state.postToEdit.commentBody

                }),
                headers: {
                    'Content-Type': 'application/json'

                }
            })

            const editedPostJSON = await editedPost.json();

            const newPostArrayWithEdited = this.state.posts.map((post) => {

              if(post._id === editedPostJSON.data._id){
                post = editedPostJSON.data
              }

              return post
            });
            this.setState({
                posts: newPostArrayWithEdited
            })

        } catch (err) {
            return err
        }    
    }



    render() {

        return (

            <div>
                <CreatePost addPost = {this.addPost} />
                <PostsList posts = {this.state.posts} deletePost ={this.deletePost} />
                <EditPost editPost = {this.editPost} postToEdit={this.state.postToEdit} />

            </div>
        )

    }



}

export default Posts;
