import React, {Component}from 'react';

class EditPost extends Component {
    constructor () {
        super();

        this.state = {
            postToEdit : {
                title: '',
                commentBody: '', 
                _id: ''
            }
        }
    }

    handleEdit = async (e) => {
        this.setState({
            postToEdit : {
                ... this.state.postToEdit,
                [e.currentTarget.name]: e.currentTarget.value
            }
        })
    }

    render() {

 return(

    <div>
       <form onSubmit={this.props.submitEditedPost}>
            <input type="text" name="title" value={this.state.postToEdit.title} onChange={this.state.handleEdit}/>
            <input type="text" name="commentBody" value={this.state.postToEdit.commentBody} onChange={this.state.handleEdit}/>
            <input type="submit" value="submit"/>

       </form>
    </div>
    )

    }


}

export default EditPost;
