import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker, InfoWindow } from 'google-maps-react';
import ReactDOM from 'react-dom';


const mapStyles = {
  width: '100%',
  height: '100%'
};

const initialCenter = {
  lat: 39.7392,
  lng: 104.9903
};


const zoom = 14;

const centerAroundCurrentLocation= false;
const visible= true;


export class MapContainer extends Component {
    constructor() {
        super();
        const { lat, lng } = initialCenter;
         this.state = {
               events : [],
               currentLocation: {
               lat: lat,
               lng: lng
            },
            showingInfoWindow: false,  
            activeMarker: {},         
            selectedPlace: {} 
          };

    }

    onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  componentDidMount() {
    if (this.props.centerAroundCurrentLocation) {
      if (navigator && navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(pos => {
          const coords = pos.coords;
          this.setState({
            currentLocation: {
              lat: coords.latitude,
              lng: coords.longitude
            }
          });
        });
      }
    }
    this.loadMap();
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.google !== this.props.google) {
      this.loadMap();
    }
    if (prevState.currentLocation !== this.state.currentLocation) {
      this.recenterMap();
    }
  }

  loadMap() {
    if (this.props && this.props.google) {
      // checks if google is available
      const { google } = this.props;
      const maps = google.maps;

      const mapRef = this.refs.map;

      // reference to the actual DOM element
      const node = ReactDOM.findDOMNode(mapRef);

      let { zoom } = this.props;
      const { lat, lng } = this.state.currentLocation;
      const center = new maps.LatLng(lat, lng);
      const mapConfig = Object.assign(
        {},
        {
          center: center,
          zoom: zoom
        }
      );
      // maps.Map() is constructor that instantiates the map
      this.map = new maps.Map(node, mapConfig);
    }
  }

  recenterMap() {
    const map = this.map;
    const current = this.state.currentLocation;

    const google = this.props.google;
    const maps = google.maps;

    if (map) {
      let center = new maps.LatLng(current.lat, current.lng);
      map.panTo(center);
    }
  }

  renderChildren() {
    const { children } = this.props;

    if (!children) return;

    return React.Children.map(children, c => {
      if (!c) return;
      return React.cloneElement(c, {
        map: this.map,
        google: this.props.google,
        mapCenter: this.state.currentLocation
      });
    });
  }


  render() {

    const allSearchResults = this.props.searchResults.map((searchResult, i) =>{
      console.log(searchResult);
      return (<Marker key={i} title={searchResult.displayName} 
        position={{lat: searchResult.location.lat,
            lng: searchResult.location.lng
        }}
       />)
    });

    return (
      <Map
        google={this.props.google}
        zoom={14}
        style={mapStyles}

     >
        <Marker
          onClick={this.onMarkerClick}
         allSearchResults= {allSearchResults}

        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
      </Map>
    );
  }

}


export default GoogleApiWrapper({
  apiKey: 'AIzaSyB3cnf0QZnOYfHCu1eElopL5grod6fyePU'
})(MapContainer);
